/* Variable CSS - Colors */
:root{
	--offgrey: #e0e0e0;
	--black: #181818;
	--beige: #eae1d4;
	--offgreen: #1f2820;
}


/* Variable CSS - Margin */
:root
{
    --mb-1: 0.5rem;
    --mb-2: 1rem;
    --mb-3: 1.5rem;
    --mb-4: 2rem;
    --mb-5: 2.5rem;
    --mb-6: 3rem;
}


* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

html,
body {
	width: 100%;
	height: 100vh;
	font-family: "Bridge Head Con Medium";
	background: var(--beige);
	font-weight: 100;
	line-height: 1;
}

::-webkit-scrollbar {
	display: none;		
}

/* Over and Under */

#over {
	width: 600px;
	z-index: 10;
	position: relative;    
  }

/* menu */

.menu {
	position: relative;
	display: flex;
	z-index: 2;
}

.hr {
	position: fixed;
	left: 50%;
	width: 3px;
	height: 100vh;
	background-color: #000;
	z-index: 3;
	transform: scaleY(0);
}

.menu-left,
.menu-right {
	position: fixed;
	width: 50%;
	height: 100vh;
	background: #0a0a0a;
	color: #fff;
	display: flex;
	justify-content: center;
	align-items: center;
	z-index: 10;
}

.menu-left {
	left: -100%;
	z-index: 10;
}

.menu-right {
	right: -100%;
	z-index: 10;
}

.menu-link a {
	text-decoration: none;
	color: #fff;
	font-size: 100px;
}

.menu-link:first-child a::before {
	display: block;
	position: relative;
	content: "";
	background-image: url("/624431d6b8cfaf6dcdde38d2906cf266.png");
	background-size: 100%;
	background-repeat: no-repeat;
	top: 80px;
	left: 0;
	height: 40px;
	width: 240px;
}

.menu-link a span {
	font-family: "Poppins";
	font-size: 24px;
}

.contact .title {
	font-size: 80px;
}

.contact .info {
	font-family: Helvetica, sans-serif;
	font-weight: lighter;
}

.mail {
	margin-bottom: 60px;
}

.menu-close {
	z-index: 2;
	position: fixed;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	cursor: pointer;
	opacity: 1;
	z-index: 11;
}

.menu-close-img {
	width: 140px;
	height: 140px;
	border-radius: 100%;
	border: 10px solid #000;
}

img {
	width: 100%;
}

.menu-open {
	position: fixed;
	top: 40px;
	right: 40px;
	padding: .3rem;
	cursor: pointer;
	font-size: 10px;
	font-weight: bolder;
	color: var(--offgreen);

	border: 2px solid;
	border-radius: 5px;

	z-index: 11;
}

.seperator {
	height: 2px;
	margin-top: 10rem;
	margin-bottom: 5rem;
}

.frameworks-seperator {
	height: 2px;
	margin-bottom: .5rem;
}

#about {
	width: auto;
	height: 100vh;
	margin-top: 0px;
	margin-left: 0;
	margin-right: auto;
	display: flex;
}

/* Section Divider */


        
.divider {								/* minor cosmetics */
	display: table; 
	font-size: calc(0vw + 50px);
	text-align: center; 
	width: 100%; 						/* divider width */
	margin: -.4em auto;					/* spacing above/below */
	color: var(--black);
}
.divider span { display: table-cell; position: relative; }
.divider span:first-child, .divider span:last-child {
	width: 50%;
	top: 26px;							/* adjust vertical align */
	-moz-background-size: 100% 2px; 	/* line width */
	background-size: 100% 2px; 			/* line width */
	background-position: 0 0, 0 100%;
	background-repeat: no-repeat;
}
.divider span:first-child {				/* color changes in here */
	background-image: -webkit-gradient(linear, 0 0, 0 100%, from(transparent), to(var(--offgreen)));
	background-image: -webkit-linear-gradient(180deg, transparent, var(--offgreen));
	background-image: -moz-linear-gradient(180deg, transparent, var(--offgreen));
	background-image: -o-linear-gradient(180deg, transparent, var(--offgreen));
	background-image: linear-gradient(90deg, transparent, var(--offgreen));
}
.divider span:nth-child(2) {
	color: #000; padding: 0px 0px; width: auto; white-space: nowrap;
}
.divider span:last-child {				/* color changes in here */
	background-image: -webkit-gradient(linear, 0 0, 0 100%, from(var(--offgreen)), to(transparent));
	background-image: -webkit-linear-gradient(180deg, var(--offgreen), transparent);
	background-image: -moz-linear-gradient(180deg, var(--offgreen), transparent);
	background-image: -o-linear-gradient(180deg, var(--offgreen), transparent);
	background-image: linear-gradient(90deg, var(--offgreen), transparent);
}

/* overlay loader */

.overlay {
	z-index: 100;
	position: fixed;
	width: 100%;
	height: 100%;
	background: var(--offgrey);
	top: 0%;
	color: var(--black);
  }
  
  .overlay h1 {
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	font-size: 25px;
	font-weight: lighter;
	letter-spacing: 20px;
  }

/* Media Queries */

@media (max-width: 992px) {
	.menu-right {
		display: none;
	}

	.menu-left {
		width: 100%;
	}

	.menu-close {
		position: fixed;
		top: 10%;
		left: 80%;
		transform: translate(-50%, -50%);
		margin: 40px;
	}

	.hr {
		display: none;
	}

	img {
		width: 40%;
	}
}

/* No mobile query */

@media screen and (max-width: 992px) {

	.nomobile {
		display:none;
	}

	.menu-close-img {
		display: none;
	}
}

